import { useState, useRef, useEffect } from "react";

interface Props {
  value: string | null;
  onChange: (date: string | null) => void;
}

const DAYS = ["SU", "MO", "TU", "WE", "TH", "FR", "SA"];

function daysInMonth(year: number, month: number): number {
  return new Date(year, month + 1, 0).getDate();
}

function formatDate(y: number, m: number, d: number): string {
  const mm = String(m + 1).padStart(2, "0");
  const dd = String(d).padStart(2, "0");
  return `${y}-${mm}-${dd}`;
}

function parseDate(s: string): { year: number; month: number; day: number } {
  const [y, m, d] = s.split("-").map(Number);
  return { year: y, month: m - 1, day: d };
}

export default function Calendar({ value, onChange }: Props) {
  const today = new Date();
  const initial = value ? parseDate(value) : null;
  const [year, setYear] = useState(initial?.year ?? today.getFullYear());
  const [month, setMonth] = useState(initial?.month ?? today.getMonth());
  const [open, setOpen] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (value) {
      const p = parseDate(value);
      setYear(p.year);
      setMonth(p.month);
    }
  }, [value]);

  useEffect(() => {
    const handleClick = (e: MouseEvent) => {
      if (ref.current && !ref.current.contains(e.target as Node)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClick);
    return () => document.removeEventListener("mousedown", handleClick);
  }, []);

  const prevMonth = () => {
    if (month === 0) {
      setMonth(11);
      setYear(year - 1);
    } else {
      setMonth(month - 1);
    }
  };

  const nextMonth = () => {
    if (month === 11) {
      setMonth(0);
      setYear(year + 1);
    } else {
      setMonth(month + 1);
    }
  };

  const selectDay = (day: number) => {
    onChange(formatDate(year, month, day));
    setOpen(false);
  };

  const clearDate = (e: React.MouseEvent) => {
    e.stopPropagation();
    onChange(null);
    setOpen(false);
  };

  const firstDayOfWeek = new Date(year, month, 1).getDay();
  const totalDays = daysInMonth(year, month);
  const todayStr = formatDate(today.getFullYear(), today.getMonth(), today.getDate());

  const displayLabel = value
    ? value
    : "NO DATE";

  return (
    <div className="calendar-wrapper" ref={ref}>
      <button
        type="button"
        className="calendar-trigger"
        onClick={() => setOpen(!open)}
      >
        {displayLabel}
      </button>
      {value && (
        <button type="button" className="calendar-clear" onClick={clearDate}>
          X
        </button>
      )}
      {open && (
        <div className="calendar-popup">
          <div className="calendar-header">
            <button type="button" onClick={prevMonth}>&lt;</button>
            <span>{year}/{String(month + 1).padStart(2, "0")}</span>
            <button type="button" onClick={nextMonth}>&gt;</button>
          </div>
          <div className="calendar-days-header">
            {DAYS.map((d) => (
              <span key={d} className={`calendar-day-label${d === "SU" ? " sun" : d === "SA" ? " sat" : ""}`}>
                {d}
              </span>
            ))}
          </div>
          <div className="calendar-grid">
            {Array.from({ length: firstDayOfWeek }).map((_, i) => (
              <span key={`empty-${i}`} className="calendar-cell empty" />
            ))}
            {Array.from({ length: totalDays }).map((_, i) => {
              const day = i + 1;
              const dateStr = formatDate(year, month, day);
              const isSelected = value === dateStr;
              const isToday = dateStr === todayStr;
              const dayOfWeek = (firstDayOfWeek + i) % 7;
              const isSun = dayOfWeek === 0;
              const isSat = dayOfWeek === 6;
              return (
                <button
                  key={day}
                  type="button"
                  className={`calendar-cell${isSelected ? " selected" : ""}${isToday ? " today" : ""}${isSun ? " sun" : ""}${isSat ? " sat" : ""}`}
                  onClick={() => selectDay(day)}
                >
                  {day}
                </button>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}

