import { useState, useEffect } from "react";
import { Todo } from "../types";
import Calendar from "./Calendar";

interface Props {
  onAdd: (title: string, dueDate: string | null) => void;
  editingTodo: Todo | null;
  onUpdate: (id: number, data: { title: string; due_date: string | null }) => void;
  onCancelEdit: () => void;
}

export default function TodoForm({ onAdd, editingTodo, onUpdate, onCancelEdit }: Props) {
  const [title, setTitle] = useState("");
  const [dueDate, setDueDate] = useState<string | null>(null);

  useEffect(() => {
    if (editingTodo) {
      setTitle(editingTodo.title);
      setDueDate(editingTodo.due_date);
    } else {
      setTitle("");
      setDueDate(null);
    }
  }, [editingTodo]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (title.trim() === "") return;

    if (editingTodo) {
      onUpdate(editingTodo.id, { title: title.trim(), due_date: dueDate });
    } else {
      onAdd(title.trim(), dueDate);
    }
    setTitle("");
    setDueDate(null);
  };

  return (
    <form className="todo-form" onSubmit={handleSubmit}>
      <div className="todo-form-row">
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="ENTER QUEST..."
        />
        <button type="submit">{editingTodo ? "SAVE" : "ADD"}</button>
        {editingTodo && (
          <button type="button" className="cancel" onClick={onCancelEdit}>
            BACK
          </button>
        )}
      </div>
      <div className="todo-form-row due-row">
        <span className="due-label">DUE:</span>
        <Calendar value={dueDate} onChange={setDueDate} />
      </div>
    </form>
  );
}

