import { Todo } from "../types";

interface Props {
  todo: Todo;
  onToggle: (id: number, completed: boolean) => void;
  onEdit: (todo: Todo) => void;
  onDelete: (id: number) => void;
}

function isOverdue(dueDate: string): boolean {
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  return new Date(dueDate + "T00:00:00") < today;
}

export default function TodoItem({ todo, onToggle, onEdit, onDelete }: Props) {
  const overdue = todo.due_date && !todo.completed && isOverdue(todo.due_date);

  return (
    <li className="todo-item">
      <input
        type="checkbox"
        checked={!!todo.completed}
        onChange={() => onToggle(todo.id, !todo.completed)}
      />
      <div className="todo-item-content">
        <span className={`title ${todo.completed ? "completed" : ""}`}>
          {todo.title}
        </span>
        {todo.due_date && (
          <span className={`due-badge${overdue ? " overdue" : ""}`}>
            {todo.due_date}
          </span>
        )}
      </div>
      <button className="edit-btn" onClick={() => onEdit(todo)}>
        EDIT
      </button>
      <button className="delete-btn" onClick={() => onDelete(todo.id)}>
        DEL
      </button>
    </li>
  );
}

