@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  image-rendering: pixelated;
}

body {
  font-family: 'Press Start 2P', monospace;
  background: #000;
  color: #fcfcfc;
  min-height: 100vh;
}

/* ── Scanline overlay ── */
body::after {
  content: '';
  position: fixed;
  inset: 0;
  background: repeating-linear-gradient(
    0deg,
    rgba(0, 0, 0, 0.15) 0px,
    rgba(0, 0, 0, 0.15) 1px,
    transparent 1px,
    transparent 3px
  );
  pointer-events: none;
  z-index: 9999;
}

/* ── NES-style pixel border mixin via box-shadow ── */
.app {
  max-width: 520px;
  margin: 32px auto;
  padding: 24px 20px;
  background: #0058f8;
  border: 4px solid #fcfcfc;
  box-shadow:
    /* outer pixel border */
    4px 0 0 0 #fcfcfc,
    -4px 0 0 0 #fcfcfc,
    0 4px 0 0 #fcfcfc,
    0 -4px 0 0 #fcfcfc,
    /* corner pixels */
    4px 4px 0 0 #000,
    -4px -4px 0 0 #000,
    4px -4px 0 0 #000,
    -4px 4px 0 0 #000,
    /* glow */
    0 0 40px rgba(0, 88, 248, 0.4);
}

h1 {
  text-align: center;
  margin-bottom: 24px;
  font-size: 18px;
  color: #f8d878;
  text-shadow:
    2px 2px 0 #a84000,
    -1px -1px 0 #000;
  letter-spacing: 2px;
  line-height: 1.6;
}

/* ── Form ── */
.todo-form {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-bottom: 20px;
}

.todo-form-row {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
}

.todo-form-row.due-row {
  align-items: center;
}

.due-label {
  font-family: 'Press Start 2P', monospace;
  font-size: 9px;
  color: #88c8f8;
  flex-shrink: 0;
}

.todo-form input {
  flex: 1;
  min-width: 0;
  padding: 10px 12px;
  font-family: 'Press Start 2P', monospace;
  font-size: 11px;
  background: #000;
  color: #fcfcfc;
  border: 3px solid #bcbcbc;
  border-radius: 0;
  outline: none;
  caret-color: #fcfcfc;
}

.todo-form input::placeholder {
  color: #585858;
}

.todo-form input:focus {
  border-color: #f8d878;
  box-shadow: 0 0 0 2px #a84000;
}

.todo-form button {
  padding: 10px 16px;
  font-family: 'Press Start 2P', monospace;
  font-size: 11px;
  background: #00a800;
  color: #fcfcfc;
  border: 3px solid #fcfcfc;
  border-radius: 0;
  cursor: pointer;
  text-shadow: 1px 1px 0 #004000;
  box-shadow:
    inset -2px -2px 0 #005800,
    inset 2px 2px 0 #00e800;
  transition: none;
}

.todo-form button:hover {
  background: #00e800;
  box-shadow:
    inset -2px -2px 0 #00a800,
    inset 2px 2px 0 #58f898;
}

.todo-form button:active {
  box-shadow:
    inset 2px 2px 0 #005800,
    inset -2px -2px 0 #00e800;
}

.todo-form button.cancel {
  background: #747474;
  text-shadow: 1px 1px 0 #303030;
  box-shadow:
    inset -2px -2px 0 #484848,
    inset 2px 2px 0 #a8a8a8;
}

.todo-form button.cancel:hover {
  background: #a8a8a8;
  box-shadow:
    inset -2px -2px 0 #747474,
    inset 2px 2px 0 #d8d8d8;
}

/* ── Todo List ── */
.todo-list {
  list-style: none;
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.empty-message {
  text-align: center;
  color: #585858;
  font-family: 'Press Start 2P', monospace;
  font-size: 10px;
  padding: 16px 0;
}

/* ── Todo Item ── */
.todo-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 10px 12px;
  background: #003088;
  border: 2px solid #6888fc;
  border-radius: 0;
  box-shadow:
    inset -2px -2px 0 #001848,
    inset 2px 2px 0 #4060c8;
  margin-bottom: 0;
}

.todo-item:hover {
  background: #0040b8;
  border-color: #88a8fc;
}

/* ── 8-bit Checkbox ── */
.todo-item input[type="checkbox"] {
  -webkit-appearance: none;
  appearance: none;
  width: 20px;
  height: 20px;
  background: #000;
  border: 3px solid #bcbcbc;
  border-radius: 0;
  cursor: pointer;
  flex-shrink: 0;
  position: relative;
}

.todo-item input[type="checkbox"]:checked {
  background: #00a800;
  border-color: #00e800;
}

.todo-item input[type="checkbox"]:checked::after {
  content: '';
  position: absolute;
  /* pixel-art checkmark via box-shadow */
  width: 2px;
  height: 2px;
  top: 2px;
  left: 2px;
  background: transparent;
  box-shadow:
    8px 0 0 #fcfcfc,
    6px 2px 0 #fcfcfc,
    4px 4px 0 #fcfcfc,
    2px 6px 0 #fcfcfc,
    0px 4px 0 #fcfcfc;
}

.todo-item input[type="checkbox"]:hover {
  border-color: #f8d878;
}

