  font-size: 11px;
  background: #00a800;
  color: #fcfcfc;
  border: 3px solid #fcfcfc;
  border-radius: 0;
  cursor: pointer;
  text-shadow: 1px 1px 0 #004000;
  box-shadow:
    inset -2px -2px 0 #005800,
    inset 2px 2px 0 #00e800;
  transition: none;
}

.todo-form button:hover {
  background: #00e800;
  box-shadow:
    inset -2px -2px 0 #00a800,
    inset 2px 2px 0 #58f898;
}

.todo-form button:active {
  box-shadow:
    inset 2px 2px 0 #005800,
    inset -2px -2px 0 #00e800;
}

.todo-form button.cancel {
  background: #747474;
  text-shadow: 1px 1px 0 #303030;
  box-shadow:
    inset -2px -2px 0 #484848,
    inset 2px 2px 0 #a8a8a8;
}

.todo-form button.cancel:hover {
  background: #a8a8a8;
  box-shadow:
    inset -2px -2px 0 #747474,
    inset 2px 2px 0 #d8d8d8;
}

/* ── Todo List ── */
.todo-list {
  list-style: none;
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.empty-message {
  text-align: center;
  color: #585858;
  font-family: 'Press Start 2P', monospace;
  font-size: 10px;
  padding: 16px 0;
}

/* ── Todo Item ── */
.todo-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 10px 12px;
  background: #003088;
  border: 2px solid #6888fc;
  border-radius: 0;
  box-shadow:
    inset -2px -2px 0 #001848,
    inset 2px 2px 0 #4060c8;
  margin-bottom: 0;
}

.todo-item:hover {
  background: #0040b8;
  border-color: #88a8fc;
}

/* ── 8-bit Checkbox ── */
.todo-item input[type="checkbox"] {
  -webkit-appearance: none;
  appearance: none;
  width: 20px;
  height: 20px;
  background: #000;
  border: 3px solid #bcbcbc;
  border-radius: 0;
  cursor: pointer;
  flex-shrink: 0;
  position: relative;
}

.todo-item input[type="checkbox"]:checked {
  background: #00a800;
  border-color: #00e800;
}

.todo-item input[type="checkbox"]:checked::after {
  content: '';
  position: absolute;
  /* pixel-art checkmark via box-shadow */
  width: 2px;
  height: 2px;
  top: 2px;
  left: 2px;
  background: transparent;
  box-shadow:
    8px 0 0 #fcfcfc,
    6px 2px 0 #fcfcfc,
    4px 4px 0 #fcfcfc,
    2px 6px 0 #fcfcfc,
    0px 4px 0 #fcfcfc;
}

.todo-item input[type="checkbox"]:hover {
  border-color: #f8d878;
}

/* ── Item Content ── */
.todo-item-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 4px;
  min-width: 0;
}

.todo-item .title {
  font-family: 'Press Start 2P', monospace;
  font-size: 10px;
  line-height: 1.6;
  color: #fcfcfc;
  word-break: break-all;
}

.todo-item .title.completed {
  text-decoration: none;
  color: #585858;
  position: relative;
}

.todo-item .title.completed::after {
  content: '';
  position: absolute;
  left: 0;
  top: 50%;
  width: 100%;
  height: 2px;
  background: #585858;
}

/* ── Due Date Badge ── */
.due-badge {
  font-family: 'Press Start 2P', monospace;
  font-size: 7px;
  color: #88c8f8;
  line-height: 1;
}

.due-badge.overdue {
  color: #fc4868;
  animation: blink 1s step-end infinite;
}

/* ── Action Buttons ── */
.todo-item button {
  padding: 6px 10px;
  font-family: 'Press Start 2P', monospace;
  font-size: 8px;
  border: 2px solid #fcfcfc;
  border-radius: 0;
  cursor: pointer;
  color: #fcfcfc;
  transition: none;
}

.todo-item .edit-btn {
  background: #f8b800;
  text-shadow: 1px 1px 0 #885000;
  box-shadow:
    inset -2px -2px 0 #a87800,
    inset 2px 2px 0 #fce000;
}

.todo-item .edit-btn:hover {
  background: #fce000;
  box-shadow:
    inset -2px -2px 0 #f8b800,
    inset 2px 2px 0 #fcf8a8;
}

.todo-item .delete-btn {
  background: #e80040;
  text-shadow: 1px 1px 0 #700020;
  box-shadow:
    inset -2px -2px 0 #a80020,
    inset 2px 2px 0 #fc4868;
}

.todo-item .delete-btn:hover {
  background: #fc4868;
  box-shadow:
    inset -2px -2px 0 #e80040,
    inset 2px 2px 0 #fc9898;
}

.todo-item button:active {
  box-shadow:
    inset 2px 2px 0 rgba(0, 0, 0, 0.4),
    inset -2px -2px 0 rgba(255, 255, 255, 0.2);
}

/* ── Blinking cursor effect on empty state ── */
.empty-message::after {
  content: '_';
  animation: blink 1s step-end infinite;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0; }
}

/* ── Responsive ── */
@media (max-width: 560px) {
  .app {
    margin: 16px 8px;
    padding: 16px 12px;
  }

  h1 {
    font-size: 14px;
  }

  .todo-form {
    flex-direction: column;
  }
}
